%flink.bsql
-- hdfs://hdfs-master:9000/hudi/flink_stream_write
drop catalog if exists paimon_catelog;
create catalog paimon_catelog with (
    'type'='paimon',
    'warehouse'='hdfs://namenode:8020/paimon_datalake'
);
use CATALOG paimon_catelog;

%flink.ssql(type=single,jobName="Full sync 15 tables(ods_mysql_base_province)",parallelism=1)
drop temporary table if exists ods_mysql_base_province;
create temporary table if not exists ods_mysql_base_province (
    id         bigint      not null comment 'id',
    name       varchar(20) null comment '省名称',
    region_id  varchar(20) null comment '大区id',
    area_code  varchar(20) null comment '行政区位码',
    iso_code   varchar(20) null comment '国际编码',
    iso_3166_2 varchar(20) null comment 'ISO3166编码',
    primary key(id) not enforced
) with (
    'connector' = 'mysql-cdc',
    'hostname' = 'mysql',
    'port' = '3306',
    'username' = 'root',
    'password' = 'mysql123457',
    'database-name' = 'edu',
    'table-name' = 'base_province'
);

drop table if exists ods_pm_base_provinceo;
create table if not exists ods_pm_base_province
with (
    'changelog-producer' = 'input',
    'bucket' = '1'
) like ods_mysql_base_province (
    EXCLUDING ALL
);
insert into ods_pm_base_province
select id, name, region_id, area_code, iso_code, iso_3166_2 
from ods_mysql_base_province;

